mapper.select:
select seq, main, type, snsAddress, addressBook_seq
from sns where 1=1 and seq = #{seq}
order by seq desc

mapper.insert:
insert into sns ( main, type, snsAddress, addressBook_seq ) values ( #{main}, #{type}, #{snsAddress}, #{addressBook_seq}
)
SELECT last_insert_id()

mapper.update:
update sns set
  main = #{main},
  type = #{type},
  snsAddress = #{snsAddress},
  addressBook_seq = #{addressBook_seq}


where seq = #{seq}
